// Extract and transform Notion page data for Slack message
const pageData = $input.first().json;

// Helper function to extract text from rich text array
function extractRichText(richTextArray) {
  if (!richTextArray || !Array.isArray(richTextArray)) return '';
  return richTextArray.map(item => item.plain_text || item.text?.content || '').join('');
}

// Helper function to extract people mentions and convert to Slack handles
function extractContributors(richTextArray) {
  if (!richTextArray || !Array.isArray(richTextArray)) return [];

  const contributors = [];
  richTextArray.forEach(item => {
    if (item.type === 'mention' && item.mention?.type === 'user') {
      // Map Notion user to Slack handle - you'll need to customize this mapping
      const userName = item.mention.user?.name || 'Unknown';
      contributors.push({
        name: userName,
        slack_handle: `@${userName.toLowerCase().replace(/\s+/g, '.')}`
      });
    }
  });

  return contributors;
}

// Helper function to extract value points from multi-line text
function extractValuePoints(text) {
  if (!text) return [];

  // Split by line breaks and filter out empty lines
  const lines = text.split('\n').filter(line => line.trim() !== '');

  // Remove bullet points or numbers if present
  return lines.map(line => line.replace(/^[â€¢\-\d\.\s]+/, '').trim()).filter(line => line !== '');
}

// Extract data from Notion page properties
const properties = pageData.properties || {};

// Extract delivery title (assuming it's the page title)
const deliveryTitle = extractRichText(properties.Name?.title || properties.Title?.title || []);

// Extract target audience (assuming there's a 'Target Audience' property)
const targetAudience = extractRichText(properties['Target Audience']?.rich_text || properties.Audience?.rich_text || []) || 'All users';

// Extract contributors (assuming there's a 'Contributors' property with user mentions)
const contributors = extractContributors(properties.Contributors?.rich_text || []);

// Extract value points (assuming there's a 'Value Points' or 'Description' property)
const valueText = extractRichText(properties['Value Points']?.rich_text || properties.Description?.rich_text || properties.Value?.rich_text || []);
const valuePoints = extractValuePoints(valueText);

// Create the delivery URL (Notion page URL)
const deliveryUrl = pageData.url || `https://notion.so/${pageData.id.replace(/-/g, '')}`;

// Return formatted data for Slack
return {
  delivery_title: deliveryTitle || 'New Delivery',
  delivery_url: deliveryUrl,
  target_audience: targetAudience,
  contributors: contributors,
  value_points: valuePoints.length > 0 ? valuePoints : ['Improved user experience'],
  page_id: pageData.id,
  event_timestamp: new Date().toISOString()
};

